import math

def ColorCodeToValue(value):
    digit = list(map(str, range(10))) + list("ABCDEF")
    a1 = digit.index(value[1]) * 16 + digit.index(value[2])
    a2 = digit.index(value[3]) * 16 + digit.index(value[4])
    a3 = digit.index(value[5]) * 16 + digit.index(value[6])
    return (a1, a2, a3)

def LinearGradient(x,y,age):
    xx=ColorCodeToValue(x)
    yy=ColorCodeToValue(y)
    ar=xx[0]
    ag=xx[1]
    ab=xx[2]
    kr=(yy[0]-xx[0])/255
    kg=(yy[1]-xx[1])/255
    kb=(yy[2]-xx[2])/255
    return(int(ar+kr*age),int(ag+kg*age),int(ab+kb*age))

def HsvToRgb(h,s,v):
    c=v*s
    x=c*(1-abs((h/60)%2-1))
    m=v-c
    cc=int((c+m)*255)
    xx=int((x+m)*255)    
    if h//60==0:
        return (cc,xx,0)
    elif h//60==1:
        return (xx,cc,0)
    elif h//60==2:
        return (0,cc,xx)    
    elif h//60==3:
        return (0,xx,cc)
    elif h//60==4:
        return (xx,0,cc)
    elif h//60==5:
        return (cc,0,xx)
    else:
        return (0,0,0)
        
def CoolColor(age):
    if age == 255:
        return (0,0,0)
    return (age,255-age,255)

def CanYouFeelTheLoveTonight(age):
    if age == 255:
        return (0,0,0)
    return LinearGradient("#B06AB3","#4568DC",age)
    
def Yoda_Black(age):
    if age == 255:
        return (0,0,0)
    return LinearGradient("#493240","#FF0099",age)

def Yoda(age):
    return LinearGradient("#493240","#FF0099",age)

def Yoda_White(age):
    if age == 255:
        return (255,255,255)
    return LinearGradient("#493240","#FF0099",age)

